import React, { Component } from 'react';
import Weather from './Weather';
import CurrentWeather from './CurrentWeather';
import axios from "axios";

class Data extends Component {
    constructor(props) {
        super(props);
        this.state = {
            currentWeather: [],
            location: '',
            weather: [],
            sunrise: '',
            sunset: ''
        }
    }

handleSubmit = (e) => {
    e.preventDefault();
    const location = e.target.elements.location.value;
    const replaced = location.replace(/ /g, '+');
    axios.get(`/weather/${replaced}`).then(response => {
        let res = response.data;
        let list = [];
        for(let i= 0; i < res.list.length; i += 2) {
            list.push([res.list[i].dt_txt,res.list[i].weather[0].icon,res.list[i].main.temp_max,res.list[i].main.temp_min]); 
        }
        this.setState({ location: res.city.name, weather: list})
    }).catch((error) => { console.log(error)});

    axios.get(`/currentweather/${replaced}`).then(response => {
        let res = response.data;
        let list = [];
        for(let i= 0; i < res.length; i += 1) {
            list.push([res.weather[0].main, res.weather[0].description])
        }
        this.setState({ currentWeather: list, sunrise: res.sys.sunrise, sunset: res.sys.sunset})
    }).catch((error) => { console.log(error)});
}

    render() { 
       console.log(this.state.weather[0])
        return ( 
            <div>
                <Weather handleSubmit={this.handleSubmit}/>
                <CurrentWeather currentweather={this.state.currentWeather} sunset={this.state.sunset} sunrise={this.state.sunrise} />
                <div>
                    <p>City: {this.state.location}</p>
                     {this.state.weather.map((item,i) =>
                     <ul>
                        <li>Date: </li>
                           {item[0]}
                        <br></br>
                        </ul>
                        )} 


                </div>
            </div>
         );
    }
}

export default Data;
