import React, { Component } from 'react';

class CurrentWeather extends Component {
    constructor(props) {
        super(props);
        this.state = {
        }; 
      } 


    render() { 
        return ( 
        <div className="top-section">

          <div className="current-weather">
          <h3>{this.props.location}</h3>

          <h2>Today</h2>
          <p>{this.props.date}</p>
                <ul>    
                <li><img src={`http://openweathermap.org/img/w/${this.props.currentWeather['icon']}.png`} alt={`${this.props.currentWeather['icon']}`}/>
                </li>
                <li>{Math.ceil(this.props.currentWeatherTemps['temp'])}&#176;</li>
                <li>Feels Like {Math.ceil(this.props.currentWeatherTemps['feels_like'])}&#176;</li>
                <li>{this.props.currentWeather['main']}</li>
                <li>{this.props.currentWeather['description']}</li>

                <li>Humidity:{this.props.currentWeatherTemps['humidity']}</li>
                <li>Wind:{this.props.currentWind}</li>
                <li>Sunrise: {this.props.sunrise}</li>
                <li>Sunset: {this.props.sunset}</li>
                </ul>
                </div>
            </div> 
         );


    }
}

export default CurrentWeather;
