import React, { Component } from 'react';
import Weather from './Weather';
import axios from "axios";

class Data extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            location: '',
            weather: []
        }
    }

handleSubmit = (e) => {
    e.preventDefault();
    const location = e.target.elements.location.value;
    const replaced = location.replace(/ /g, '+');
    axios.get(`/weather/${replaced}`).then(response => {
        let res = response.data;
        this.setState({ items: res, location: res.city.name, weather: res.list})
    }).catch((error) => { console.log(error)});
}

    render() { 
        console.log(this.state.items)
        return ( 
            <div>
                <Weather handleSubmit={this.handleSubmit}/>
                <div>
                    <p>City: {this.state.location}</p>

                    <ul>
                    {/* {this.state.items.map((item,i) =>
                        <li>{item.main.temp}</li>
                        )} */}
                    </ul>
                </div>
            </div>
         );
    }
}

export default Data;
