import React, { Component } from 'react';
import Weather from './Weather';
import CurrentWeather from './CurrentWeather';
import WeekWeather from './WeekWeather';
import Places from './Places';
import axios from "axios";

const moment = require('moment-timezone');

class Data extends Component {
    constructor(props) {
        super(props);
        this.state = {
            currentWeather: [],
            places: [],
            currentWeatherTemps: [],
            currentWind: '',
            location: '',
            weather: [],
            sunrise: '',
            sunset: ''
        }
    }

handleSubmit = (latitude,longitude, cityname) => {
    let lat = latitude.toString();
    let long = longitude.toString();
    let cityName = cityname;
    let LatLng = lat.concat(',',long);
    let WeatherLatLng = `lat=${lat}&lon=${long}`;

    axios.get(`/places/${LatLng}`).then(response => {
        let res = response.data;
        this.setState({places: res.businesses})
    }).catch((error) => { console.log(error)});

    axios.get(`/weather/${WeatherLatLng}`).then(response => {
        let res = response.data;
        let list = [];
        for(let i= 0; i < res.list.length; i += 8) {
            list.push([res.list[i].dt_txt,res.list[i].weather[0].icon,res.list[i].weather[0].main,Math.ceil(res.list[i].main.temp_max),Math.floor(res.list[i].main.temp_min)]); 
        }
        this.setState({ location: cityName, weather: list})
    }).catch((error) => { console.log(error)});

    axios.get(`/currentweather/${WeatherLatLng}`).then(response => {
        let res = response.data;
        this.setState({ currentWeather: res.weather[0], currentWeatherTemps: res.main, currentWind: res.wind.speed, sunrise: `${this.getTimes(res.sys.sunrise, res.timezone)} AM`, sunset: `${this.getTimes(res.sys.sunset, res.timezone)} PM`})

    }).catch((error) => { console.log(error)});
}

getTimes = (time,tzoffset) => {
    const srTime = new Date((time+tzoffset)*1000);
    const twoDigits = (val) => {
        return ('0' + val).slice(-2);
      };

    let hours = twoDigits(srTime.getUTCHours());
    hours > 12 ? hours = hours - 12 : hours = hours;
    const minutes = twoDigits(srTime.getUTCMinutes());
    const seconds = twoDigits(srTime.getUTCSeconds());
    const sunriseTime =`${hours}:${minutes}`
    return sunriseTime;
}

    render() { 
        return ( 
            <div>
                <Weather handleSubmit={this.handleSubmit.bind(this)}/>
                <CurrentWeather location={this.state.location} currentWeather={this.state.currentWeather} currentWeatherTemps={this.state.currentWeatherTemps} currentWind={this.state.currentWind} sunset={this.state.sunset} sunrise={this.state.sunrise} />
                <WeekWeather weather={this.state.weather} />
                <Places places={this.state.places} />
            </div>
         );
    }
}

export default Data;
