import React, { Component } from 'react';
import Weather from './Weather';
import CurrentWeather from './CurrentWeather';
import WeekWeather from './WeekWeather';
import axios from "axios";



class Data extends Component {
    constructor(props) {
        super(props);
        this.state = {
            currentWeather: [],
            yelp: [],
            currentWeatherTemps: [],
            currentWind: '',
            location: '',
            weather: [],
            sunrise: '',
            sunset: ''
        }
    }


handleSubmit = (e) => {
    e.preventDefault();
    const location = e.target.elements.location.value;
    const replaced = location.replace(/ /g, '+');

    axios.get(`/places/${replaced}`).then(response => {
        let res = response.data;
        this.setState({yelp: res})
    }).catch((error) => { console.log(error)});

    axios.get(`/weather/${replaced}`).then(response => {
        let res = response.data;
        let list = [];
        for(let i= 0; i < res.list.length; i += 8) {
            list.push([res.list[i].dt_txt,res.list[i].weather[0].icon,res.list[i].main.temp_max,res.list[i].main.temp_min]); 
        }
        this.setState({ location: res.city.name, weather: list})
    }).catch((error) => { console.log(error)});

    axios.get(`/currentweather/${replaced}`).then(response => {
        let res = response.data;
        this.setState({ currentWeather: res.weather[0], currentWeatherTemps: res.main, currentWind: res.wind.speed, sunrise: res.sys.sunrise, sunset: res.sys.sunset})
    }).catch((error) => { console.log(error)});

}

    render() { 
       console.log(this.state.weather[0])
        return ( 
            <div>
                <Weather handleSubmit={this.handleSubmit}/>
                <CurrentWeather location={this.state.location} currentWeather={this.state.currentWeather} currentWeatherTemps={this.state.currentWeatherTemps} currentWind={this.state.currentWind} sunset={this.state.sunset} sunrise={this.state.sunrise} />
                <WeekWeather weather={this.state.weather} />

            </div>
         );
    }
}

export default Data;
